<?php

namespace App\Http\Controllers\Admin;

use App\Http\Controllers\Controller;
use App\Models\Product;
use Illuminate\Http\Request;

class ProductController extends Controller
{
    public function index(Request $request)
    {
        $limit = $request->get('limit', 10);
        $skip = $request->get('skip', 0);

        $products = Product::with('category')->skip($skip)->take($limit)->get();

        $products = $products->map(function ($product) {
            $data = $product->toArray();
            $data['category'] = $product->category ? $product->category->slug : null;
            // Convert snake_case to camelCase for frontend
            $data['discountPercentage'] = $data['discount_percentage'] ?? 0;
            unset($data['discount_percentage'], $data['category_id'], $data['created_at'], $data['updated_at']);
            return $data;
        });

        return response()->json($products);
    }

    public function search(Request $request)
    {
        $query = $request->get('q', '');
        $limit = $request->get('limit', 10);
        $skip = $request->get('skip', 0);

        if (empty(trim($query))) {
            // If query is empty, return empty results
            return response()->json([]);
        }

        // Build search query (case-insensitive)
        $searchQuery = Product::with('category')
            ->where(function ($q) use ($query) {
                $q->whereRaw('LOWER(title) LIKE ?', ['%' . strtolower($query) . '%'])
                  ->orWhereRaw('LOWER(description) LIKE ?', ['%' . strtolower($query) . '%'])
                  ->orWhereRaw('LOWER(brand) LIKE ?', ['%' . strtolower($query) . '%']);
            });

        // Get total count (before pagination)
        $total = $searchQuery->count();

        // Get paginated products
        $products = $searchQuery
            ->skip($skip)
            ->take($limit)
            ->get();

        $products = $products->map(function ($product) {
            $data = $product->toArray();
            $data['category'] = $product->category ? $product->category->slug : null;
            // Convert snake_case to camelCase for frontend
            $data['discountPercentage'] = $data['discount_percentage'] ?? 0;
            unset($data['discount_percentage'], $data['category_id'], $data['created_at'], $data['updated_at']);
            return $data;
        });

        return response()->json($products);
    }

    public function show($id)
    {
        $product = Product::with('category')->findOrFail($id);

        $data = $product->toArray();
        $data['category'] = $product->category ? $product->category->slug : null;
        // Convert snake_case to camelCase for frontend
        $data['discountPercentage'] = $data['discount_percentage'] ?? 0;
        unset($data['discount_percentage'], $data['category_id'], $data['created_at'], $data['updated_at']);

        return response()->json($data);
    }

    public function getCategories()
    {
        // Return categories with hierarchy information
        $categories = \App\Models\Category::with('parent')->get();

        $formatted = $categories->map(function ($category) {
            $result = [
                'id' => $category->id,
                'name' => $category->name,
                'slug' => $category->slug,
            ];
            
            if ($category->parent) {
                $result['parent'] = [
                    'id' => $category->parent->id,
                    'name' => $category->parent->name,
                    'slug' => $category->parent->slug,
                ];
            }
            
            return $result;
        });

        return response()->json($formatted);
    }

    public function getByCategory($category, Request $request)
    {
        $limit = $request->get('limit', 10);
        $skip = $request->get('skip', 0);

        $categoryModel = \App\Models\Category::where('slug', $category)->firstOrFail();

        // Get subcategory IDs if this category has children
        $subcategoryIds = \App\Models\Category::where('parent_id', $categoryModel->id)->pluck('id');
        
        // Get products from this category and its subcategories
        $categoryIds = collect([$categoryModel->id])->merge($subcategoryIds);

        $products = Product::with('category')
            ->whereIn('category_id', $categoryIds)
            ->skip($skip)
            ->take($limit)
            ->get();

        $products = $products->map(function ($product) {
            $data = $product->toArray();
            $data['category'] = $product->category ? $product->category->slug : null;
            // Convert snake_case to camelCase for frontend
            $data['discountPercentage'] = $data['discount_percentage'] ?? 0;
            unset($data['discount_percentage'], $data['category_id'], $data['created_at'], $data['updated_at']);
            return $data;
        });

        return response()->json($products);
    }

    public function store(Request $request)
    {
        $request->validate([
            'title' => 'required|string',
            'description' => 'nullable|string',
            'price' => 'required|numeric',
            'discount_percentage' => 'nullable|numeric',
            'rating' => 'nullable|numeric',
            'stock' => 'nullable|integer',
            'brand' => 'nullable|string',
            'category' => 'required|string',
            'thumbnail' => 'nullable|string',
            'images' => 'nullable|array',
        ]);

        $category = \App\Models\Category::where('slug', $request->category)->firstOrFail();

        $data = $request->all();
        $data['category_id'] = $category->id;
        unset($data['category']);

        $product = Product::create($data);

        $product->load('category');
        $data = $product->toArray();
        $data['category'] = $product->category ? $product->category->slug : null;
        // Convert snake_case to camelCase for frontend
        $data['discountPercentage'] = $data['discount_percentage'] ?? 0;
        unset($data['discount_percentage'], $data['category_id'], $data['created_at'], $data['updated_at']);

        return response()->json($data, 201);
    }

    public function update(Request $request, $id)
    {
        $product = Product::findOrFail($id);

        $request->validate([
            'title' => 'required|string',
            'description' => 'nullable|string',
            'price' => 'required|numeric',
            'discount_percentage' => 'nullable|numeric',
            'rating' => 'nullable|numeric',
            'stock' => 'nullable|integer',
            'brand' => 'nullable|string',
            'category' => 'required|string',
            'thumbnail' => 'nullable|string',
            'images' => 'nullable|array',
        ]);

        $category = \App\Models\Category::where('slug', $request->category)->firstOrFail();

        $data = $request->all();
        $data['category_id'] = $category->id;
        unset($data['category']);

        $product->update($data);

        $product->load('category');
        $data = $product->toArray();
        $data['category'] = $product->category ? $product->category->slug : null;
        // Convert snake_case to camelCase for frontend
        $data['discountPercentage'] = $data['discount_percentage'] ?? 0;
        unset($data['discount_percentage'], $data['category_id'], $data['created_at'], $data['updated_at']);

        return response()->json($data);
    }

    public function destroy($id)
    {
        $product = Product::findOrFail($id);
        $product->delete();

        return response()->json(['message' => 'Product deleted']);
    }
}